import React, { useState, useEffect } from 'react';
import { Cloud } from 'lucide-react';
import { fetchGames } from './api/gameApi';

export default function App() {
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadGames = async () => {
      const data = await fetchGames();
      setGames(data);
      setLoading(false);
    };
    loadGames();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-slate-900 text-white flex items-center justify-center">
        <div className="text-center">
          <Cloud className="w-16 h-16 animate-bounce mx-auto mb-4" />
          <h1 className="text-2xl font-bold">Loading CloudBox...</h1>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-900 text-white">
      <header className="bg-slate-800 p-4 flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div className="bg-gradient-to-br from-blue-500 to-purple-600 p-2 rounded-lg">
            <Cloud className="text-white w-6 h-6" />
          </div>
          <h1 className="text-2xl font-bold">CloudBox</h1>
        </div>
        <p className="text-sm opacity-70">Cloud Gaming Catalog</p>
      </header>

      <main className="p-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {games.map(game => (
            <div key={game.id} className="bg-slate-800 rounded-lg p-4 shadow-lg">
              <img 
                src={game.imageUrl} 
                alt={game.title}
                className="w-full h-48 object-cover rounded mb-4"
              />
              <h2 className="text-xl font-bold mb-2">{game.title}</h2>
              <p className="text-sm opacity-70 mb-2">{game.genre} • {game.year}</p>
              <p className="text-sm">{game.description.substring(0, 100)}...</p>
              <div className="mt-4 flex justify-between items-center">
                <span className={`px-3 py-1 rounded-full text-sm ${game.service === 'xbox' ? 'bg-blue-600' : 'bg-green-600'}`}>
                  {game.service === 'xbox' ? 'Xbox Cloud' : 'GeForce NOW'}
                </span>
                <span className="text-yellow-400">★ {game.score}/10</span>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
