// src/api/gameApi.js - CloudBox Game API Client

/**
 * Fetches games from the CloudBox backend API
 * Falls back to sample data if backend is not available
 */
export async function fetchGames() {
    try {
        // Try to fetch from your backend API
        const response = await fetch('http://localhost:5000/api/games');
        
        if (response.ok) {
            const data = await response.json();
            return data.games || getFallbackGames();
        } else {
            console.warn('CloudBox API not available, using fallback data');
            return getFallbackGames();
        }
    } catch (error) {
        console.warn('Network error, using fallback data:', error.message);
        return getFallbackGames();
    }
}

/**
 * Fallback games data for when API is unavailable
 */
function getFallbackGames() {
    return [
        {
            id: '1',
            title: 'Cyberpunk 2077',
            service: 'gfn',
            imageUrl: 'https://images.igdb.com/igdb/image/upload/t_cover_big/co4a7y.jpg',
            description: 'An open-world RPG set in Night City',
            publisher: 'CD Projekt Red',
            genre: 'RPG',
            year: 2020,
            score: 8.5,
            url: 'https://www.nvidia.com/en-us/geforce-now/games/',
            color: 'from-green-400 to-green-700'
