import React, { useState, useEffect, useRef, useCallback } from 'react';
import { 
  Gamepad2, 
  Search, 
  Settings, 
  Filter, 
  Monitor, 
  Moon, 
  Sun, 
  X,
  Cloud,
  ArrowDownAZ,
  ArrowUpAZ,
  ExternalLink,
  ImageOff,
  Star,
  Calendar,
  Layers,
  Info,
  Rocket,
  Menu,
  Loader2
} from 'lucide-react';
import { fetchGames } from './api/gameApi';

// Platform Logo Components
const XboxLogo = ({ className }) => (
  <svg viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg" className={className}>
      <path d="m24 12c0-.001 0-.001 0-.002 0-3.618-1.606-6.861-4.144-9.054l-.015-.013c-1.91 1.023-3.548 2.261-4.967 3.713l-.004.004c.044.046.087.085.131.132 3.719 4.012 7.106 9.73 6.546 12.471 1.53-1.985 2.452-4.508 2.452-7.246 0-.002 0-.004 0-.006z"/>
      <path d="m12.591 3.955c1.68-1.104 3.699-1.833 5.872-2.022l.048-.003c-1.837-1.21-4.09-1.929-6.511-1.929-2.171 0-4.207.579-5.962 1.591l.058-.031c.658.567 2.837.781 5.484 2.4.143.089.316.142.502.142.189 0 .365-.055.513-.149l-.004.002z"/>
      <path d="m9.166 6.778c.046-.049.093-.09.138-.138-1.17-1.134-2.446-2.174-3.806-3.1l-.099-.064c-.302-.221-.681-.354-1.091-.354-.146 0-.288.017-.425.049l.013-.002c-2.398 2.198-3.896 5.344-3.896 8.84 0 2.909 1.037 5.576 2.762 7.651l-.016-.020c-1.031-2.547 2.477-8.672 6.419-12.862z"/>
      <path d="m12.084 9.198c-3.962 3.503-9.477 8.73-8.632 11.218 2.174 2.213 5.198 3.584 8.542 3.584 3.493 0 6.637-1.496 8.826-3.883l.008-.009c.486-2.618-4.755-7.337-8.744-10.910z"/>
  </svg>
);

const GfnLogo = ({ className }) => (
  <svg viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg" className={className}>
      <path d="M8.948 8.798v-1.43a6.7 6.7 0 0 1 .424-.018c3.922-.124 6.493 3.374 6.493 3.374s-2.774 3.851-5.75 3.851c-.398 0-.787-.062-1.158-.185v-4.346c1.528.185 1.837.857 2.747 2.385l2.04-1.714s-1.492-1.952-4-1.952a6.016 6.016 0 0 0-.796.035m0-4.735v2.138l.424-.027c5.45-.185 9.01 4.47 9.01 4.47s-4.08 4.964-8.33 4.964c-.37 0-.733-.035-1.095-.097v1.325c.3.035.61.062.91.062 3.957 0 6.82-2.023 9.593-4.408.459.371 2.34 1.263 2.73 1.652-2.633 2.208-8.772 3.984-12.253 3.984-.335 0-.653-.018-.971-.053v1.864H24V4.063zm0 10.326v1.131c-3.657-.654-4.673-4.46-4.673-4.46s1.758-1.944 4.673-2.262v1.237H8.94c-1.528-.186-2.73 1.245-2.73 1.245s.68 2.412 2.739 3.11M2.456 10.9s2.164-3.197 6.5-3.533V6.201C4.153 6.59 0 10.653 0 10.653s2.35 6.802 8.948 7.42v-1.237c-4.84-.6-6.492-5.936-6.492-5.936z"/>
  </svg>
);

// Pulsing Glow Styles
const PulsingGlowStyles = () => (
    <style dangerouslySetInnerHTML={{ __html: `
        @keyframes pulse-glow {
            0%, 100% { 
                box-shadow: 
                    0 0 5px rgba(99, 102, 241, 0.3), 
                    0 0 10px rgba(99, 102, 241, 0.15); 
            }
            50% { 
                box-shadow: 
                    0 0 10px rgba(129, 140, 248, 0.5), 
                    0 0 20px rgba(129, 140, 248, 0.3); 
            }
        }
        .is-visually-active {
            transform: scale(1.05) !important;
            z-index: 20; 
            border-color: #6366F1;
            animation: pulse-glow 2s infinite alternate ease-in-out;
        }
        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }
        .animate-spin-slow {
            animation: spin 3s linear infinite;
        }
    `}} />
);

// Controller Guide
const ControllerGuide = ({ darkMode }) => {
    const buttonClass = "w-6 h-6 rounded-full flex items-center justify-center font-bold text-xs shadow-inner border-2";
    const buttonBorder = darkMode ? 'border-white/80 text-white' : 'border-gray-500 text-gray-900';
    
    return (
        <div className={`fixed bottom-4 right-4 z-50 p-3 rounded-xl border-2 ${darkMode ? 'border-white/20 bg-black/10 text-white' : 'border-gray-300 bg-white/10 text-gray-900'} transition-all duration-300`}>
            <div className="flex flex-col gap-1 text-sm font-medium">
                <div className="flex items-center gap-3">
                    <div className="flex items-center gap-2">
                        <span className={`${buttonClass} ${buttonBorder}`}>X</span>
                        <span>Launch Game</span>
                    </div>
                    <div className="flex items-center gap-2">
                        <span className={`${buttonClass} ${buttonBorder}`}>
                           <Menu className="w-4 h-4" />
                        </span>
                        <span>Settings</span>
                    </div>
                </div>
                <div className="flex items-center gap-3 pt-1 border-t border-white/20">
                    <div className="flex items-center gap-2">
                        <span className={`${buttonClass} ${buttonBorder}`}>A</span>
                        <span>Game Info</span>
                    </div>
                    <div className="flex items-center gap-2">
                        <span className={`${buttonClass} ${buttonBorder}`}>Y</span>
                        <span>Search</span>
                    </div>
                </div>
            </div>
        </div>
    );
};

// Game Detail Modal
const GameDetailModal = ({ game, onClose, onLaunch, darkMode }) => {
    if (!game) return null;

    const logoBgClass = game.service === 'xbox' 
        ? 'bg-[#107C10] text-white'
        : 'bg-[#76B900] text-white';
    
    const serviceLogo = game.service === 'xbox' 
        ? <XboxLogo className="w-6 h-6" /> 
        : <GfnLogo className="w-6 h-6" />;

    const modalContentClasses = darkMode 
        ? 'bg-slate-800/95 text-white border border-slate-700' 
        : 'bg-white/95 text-gray-900 border border-gray-200';

    return (
        <div 
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 transition-opacity duration-300 overflow-y-auto p-4"
            onClick={onClose}
        >
            <div className="absolute inset-0 bg-black/60" onClick={onClose} />
            <div 
                className={`relative w-full max-w-2xl max-h-[90vh] rounded-xl shadow-2xl p-6 transition-transform duration-300 transform scale-100 ${modalContentClasses} flex flex-col`}
                onClick={(e) => e.stopPropagation()}
            >
                <div className="flex justify-between items-start mb-6 flex-shrink-0">
                    <div className="flex flex-col">
                        <div className={`p-2 rounded-full mb-2 self-start ${logoBgClass}`}>
                            {serviceLogo}
                        </div>
                        <h2 className="text-3xl font-extrabold leading-tight">{game.title}</h2>
                    </div>
                    <button onClick={onClose} className={`p-2 rounded-full transition-colors ${darkMode ? 'hover:bg-slate-700' : 'hover:bg-gray-200'}`}>
                        <X className="w-6 h-6" />
                    </button>
                </div>

                <div className="relative flex-grow overflow-y-auto pr-2">
                    <div className="flex flex-col md:flex-row gap-6">
                        <div className="flex-shrink-0 mb-4 md:mb-0 rounded-lg overflow-hidden shadow-xl aspect-[2/3] w-full max-w-[150px] self-center md:self-start">
                            <img 
                                src={game.imageUrl} 
                                alt={`${game.title} cover`} 
                                className="w-full h-full object-cover"
                                onError={(e) => {
                                    e.target.onerror = null;
                                    const parent = e.target.parentNode;
                                    parent.innerHTML = `
                                        <div class="w-full h-full flex flex-col items-center justify-center bg-gradient-to-br ${game.color} p-4">
                                            <ImageOff class="w-10 h-10 text-white/50 mb-2" />
                                            <span class="text-white/70 text-sm text-center">${game.title}</span>
                                        </div>
                                    `;
                                }}
                            />
                        </div>

                        <div>
                            <div className="flex flex-wrap gap-2 mb-6">
                                <div className={`flex items-center gap-1 text-sm px-3 py-1 rounded-full ${darkMode ? 'bg-indigo-600/70' : 'bg-indigo-500 text-white'}`}>
                                    <Star className="w-4 h-4" />
                                    <span>{game.score} / 10</span>
                                </div>
                                <div className={`flex items-center gap-1 text-sm px-3 py-1 rounded-full ${darkMode ? 'bg-slate-700' : 'bg-gray-200 text-gray-700'}`}>
                                    <Calendar className="w-4 h-4" />
                                    <span>{game.year}</span>
                                </div>
                                <div className={`flex items-center gap-1 text-sm px-3 py-1 rounded-full ${darkMode ? 'bg-slate-700' : 'bg-gray-200 text-gray-700'}`}>
                                    <Layers className="w-4 h-4" />
                                    <span>{game.genre}</span>
                                </div>
                                <div className={`flex items-center gap-1 text-sm px-3 py-1 rounded-full ${game.service === 'xbox' ? 'bg-[#107C10]/70' : 'bg-[#76B900]/70'} text-white`}>
                                    {game.service === 'xbox' ? <XboxLogo className="w-4 h-4" /> : <GfnLogo className="w-4 h-4" />}
                                    <span>{game.service === 'xbox' ? 'Xbox Cloud' : 'GeForce NOW'}</span>
                                </div>
                            </div>

                            <h3 className="text-lg font-semibold mb-2 opacity-70">About the Game</h3>
                            <p className="text-sm leading-relaxed mb-8 opacity-90">{game.description}</p>
                            
                            <div className="grid grid-cols-2 gap-4 mb-6">
                                <div>
                                    <h4 className="text-sm font-semibold mb-1 opacity-70">Publisher</h4>
                                    <p className="text-sm">{game.publisher}</p>
                                </div>
                                <div>
                                    <h4 className="text-sm font-semibold mb-1 opacity-70">Release Year</h4>
                                    <p className="text-sm">{game.year}</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="flex-shrink-0 pt-4 flex gap-4">
                    <button 
                        onClick={() => {
                            console.log('Launching game:', game.title, 'to:', game.url);
                            if (game.url && game.url !== '#') {
                                window.open(game.url, '_blank', 'noopener,noreferrer');
                            }
                            onClose();
                        }}
                        className="w-full flex items-center justify-center gap-2 p-4 rounded-xl text-lg font-bold text-white bg-blue-600 hover:bg-blue-700 transition-all duration-200 shadow-lg shadow-blue-500/50 transform hover:scale-[1.005]"
                    >
                        <ExternalLink className="w-5 h-5" />
                        Launch Game (X)
                    </button>
                </div>
            </div>
        </div>
    );
};

// Settings Modal - FIXED
const SettingsModal = ({ show, onClose, darkMode, setDarkMode, services, setServices, sortAsc, setSortAsc }) => {
    if (!show) return null;

    const modalClasses = darkMode 
        ? 'bg-slate-800 text-white border border-slate-700' 
        : 'bg-white text-gray-900 border border-gray-200';

    return (
        <div 
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm transition-opacity duration-300"
            onClick={onClose}
        >
            <div 
                className={`w-full max-w-lg rounded-xl shadow-2xl p-6 transition-transform duration-300 transform scale-100 ${modalClasses}`}
                onClick={(e) => e.stopPropagation()}
            >
                <div className="flex justify-between items-center mb-6">
                    <h2 className="text-2xl font-bold flex items-center gap-2">
                        <Settings className="w-6 h-6" />
                        CloudBox Settings
                    </h2>
                    <button 
                        onClick={onClose} 
                        className={`p-2 rounded-full transition-colors ${darkMode ? 'hover:bg-slate-700' : 'hover:bg-gray-200'}`}
                    >
                        <X className="w-6 h-6" />
                    </button>
                </div>
                
                <div className="mb-6 p-4 rounded-lg bg-slate-700/50">
                    <h3 className="text-lg font-semibold mb-2">Theme</h3>
                    <div className="flex items-center justify-between">
                        <span>Toggle Dark Mode</span>
                        <button 
                            onClick={() => setDarkMode(!darkMode)}
                            className={`flex items-center p-2 rounded-full transition-colors ${darkMode ? 'bg-indigo-600 hover:bg-indigo-700' : 'bg-gray-200 text-gray-800 hover:bg-gray-300'}`}
                        >
                            {darkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
                            <span className="ml-2 text-sm font-medium">{darkMode ? 'Light Mode' : 'Dark Mode'}</span>
                        </button>
                    </div>
                </div>

                <div className="mb-6 p-4 rounded-lg bg-slate-700/50">
                    <h3 className="text-lg font-semibold mb-2 flex items-center gap-2"><Filter className="w-5 h-5" /> Filters</h3>
                    <div className="flex gap-4">
                        <button
                            onClick={() => setServices(prev => ({ ...prev, xbox: !prev.xbox }))}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${services.xbox 
                                ? 'bg-[#107C10] text-white shadow-md' 
                                : 'bg-slate-600 text-gray-300 hover:bg-slate-500'}`}
                        >
                            <XboxLogo className="w-5 h-5 mr-2" />
                            Xbox Cloud
                        </button>
                        <button
                            onClick={() => setServices(prev => ({ ...prev, gfn: !prev.gfn }))}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${services.gfn 
                                ? 'bg-[#76B900] text-white shadow-md' 
                                : 'bg-slate-600 text-gray-300 hover:bg-slate-500'}`}
                        >
                            <GfnLogo className="w-5 h-5 mr-2" />
                            GeForce NOW
                        </button>
                    </div>
                </div>

                <div className="mb-6 p-4 rounded-lg bg-slate-700/50">
                    <h3 className="text-lg font-semibold mb-2 flex items-center gap-2"><ArrowDownAZ className="w-5 h-5" /> Sorting</h3>
                    <div className="flex gap-4">
                        <button
                            onClick={() => setSortAsc(true)}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${sortAsc
                                ? 'bg-indigo-600 text-white shadow-md' 
                                : 'bg-slate-600 text-gray-300 hover:bg-slate-500'}`}
                        >
                            <ArrowUpAZ className="w-5 h-5 mr-2" />
                            A-Z (Ascending)
                        </button>
                        <button
                            onClick={() => setSortAsc(false)}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${!sortAsc
                                ? 'bg-indigo-600 text-white shadow-md' 
                                : 'bg-slate-600 text-gray-300 hover:bg-slate-500'}`}
                        >
                            <ArrowDownAZ className="w-5 h-5 mr-2" />
                            Z-A (Descending)
                        </button>
                    </div>
                </div>
                
                <button 
                    onClick={onClose}
                    className="mt-4 w-full p-3 rounded-lg font-bold bg-indigo-500 hover:bg-indigo-600 text-white transition-colors"
                >
                    Close Settings (B)
                </button>
            </div>
        </div>
    );
}

// Main App Component - FIXED VERSION
export default function App() {
  const [games, setGames] = useState([]);
  const [filteredGames, setFilteredGames] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedGame, setSelectedGame] = useState(null);
  const [darkMode, setDarkMode] = useState(true);
  const [showSettings, setShowSettings] = useState(false);
  const [services, setServices] = useState({ xbox: true, gfn: true });
  const [sortAsc, setSortAsc] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [focusedIndex, setFocusedIndex] = useState(0);
  const [isGridFocused, setIsGridFocused] = useState(true);
  const gridRef = useRef(null);
  const searchInputRef = useRef(null);

  // Fetch games on component mount
  useEffect(() => {
    const loadGames = async () => {
      try {
        const data = await fetchGames();
        console.log('Loaded games:', data.length);
        setGames(data);
      } catch (error) {
        console.error("Failed to fetch games:", error);
      } finally {
        setIsLoading(false);
      }
    };

    loadGames();
  }, []);

  // Filter and sort games - FIXED: Now shows Xbox games
  useEffect(() => {
    if (isLoading) return;

    console.log('Filtering games with services:', services);

    let result = games.filter(g => {
      const matchesService = (g.service === 'xbox' && services.xbox) || 
                            (g.service === 'gfn' && services.gfn);
      const matchesSearch = searchQuery ? 
        g.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        g.genre.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (g.publisher && g.publisher.toLowerCase().includes(searchQuery.toLowerCase()))
        : true;
      
      return matchesService && matchesSearch;
    });

    console.log('Filtered to', result.length, 'games');

    result.sort((a, b) => {
      return sortAsc ? a.title.localeCompare(b.title) : b.title.localeCompare(a.title);
    });

    setFilteredGames(result);
    
    if (result.length > 0) {
        setFocusedIndex(prev => Math.min(prev, result.length - 1));
    } else {
        setFocusedIndex(0);
    }
    
    if (selectedGame && !result.find(g => g.id === selectedGame.id)) {
        setSelectedGame(null);
    }
  }, [games, services, sortAsc, searchQuery, selectedGame, isLoading]);

  // Scroll into view
  useEffect(() => {
    if (isGridFocused && gridRef.current && filteredGames.length > 0) {
      const cards = gridRef.current.querySelectorAll('[data-game-card]');
      if (cards[focusedIndex]) {
        cards[focusedIndex].scrollIntoView({ behavior: 'smooth', block: 'center', inline: 'nearest' });
      }
    }
  }, [focusedIndex, isGridFocused, filteredGames.length]);

  // Launch handler - FIXED: Actually opens URL
  const handleLaunch = useCallback((game, directLaunch = false) => {
    if (!game) return;
    
    if (directLaunch) {
        console.log('Launching game:', game.title, 'to:', game.url);
        if (game.url && game.url !== '#') {
            window.open(game.url, '_blank', 'noopener,noreferrer');
        }
        setSelectedGame(null);
        return;
    }

    if (!selectedGame || selectedGame.id !== game.id) {
        setSelectedGame(game);
        return;
    }
  }, [selectedGame]);

  // Navigation handler - FIXED
  const handleNavigation = useCallback((direction) => {
    if (showSettings || selectedGame || document.activeElement === searchInputRef.current) return;
    if (!isGridFocused) setIsGridFocused(true);

    const total = filteredGames.length;
    if (total === 0) return;

    let gridWidth = 4;
    if (gridRef.current) {
        const style = window.getComputedStyle(gridRef.current);
        const columns = style.getPropertyValue('grid-template-columns').split(' ').length;
        if (columns > 0) gridWidth = columns;
    }
    
    setFocusedIndex(prev => {
      let next = prev;
      switch (direction) {
        case 'UP': next = prev - gridWidth; break;
        case 'DOWN': next = prev + gridWidth; break;
        case 'LEFT': next = prev - 1; break;
        case 'RIGHT': next = prev + 1; break;
        default: break;
      }
      
      if (next < 0) next = 0;
      if (next >= total) next = total - 1;
      return next;
    });
  }, [filteredGames, isGridFocused, showSettings, selectedGame]);

  // Keyboard listeners - FIXED
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Prevent default for game controls
      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', ' ', 'Enter', 'a', 'x', 'y', 'b'].includes(e.key)) {
        e.preventDefault();
      }

      // Escape/B always closes the top-most modal/panel
      if (e.key === 'Escape' || e.key === 'b') {
        if (selectedGame) {
          setSelectedGame(null);
        } else if (showSettings) {
          setShowSettings(false);
        } else if (document.activeElement === searchInputRef.current) {
          searchInputRef.current.blur();
          setIsGridFocused(true);
        }
        return;
      }

      // Start/Enter opens settings
      if ((e.key === 'Enter' && document.activeElement !== searchInputRef.current && !selectedGame && !showSettings) || e.key === 's') {
        setShowSettings(true);
        return;
      }
      
      if (showSettings) return;

      const gameInFocus = filteredGames[focusedIndex];

      switch(e.key) {
        case 'ArrowUp': 
        case 'ArrowDown': 
        case 'ArrowLeft': 
        case 'ArrowRight': 
          if (!selectedGame) handleNavigation(e.key.replace('Arrow', '').toUpperCase());
          break;
        case 'Enter': 
        case 'a': // A button: Open modal
          if (selectedGame) {
            handleLaunch(selectedGame, true);
          } else if (gameInFocus && document.activeElement !== searchInputRef.current) {
            setSelectedGame(gameInFocus);
          }
          break;
        case 'x': // X button: Launch directly
        case ' ': // Spacebar: Launch directly
          if (selectedGame) {
            handleLaunch(selectedGame, true);
          } else if (gameInFocus) {
            handleLaunch(gameInFocus, true);
          }
          break;
        case 'y': // Y button: Focus search
          if (!selectedGame && !showSettings && searchInputRef.current) {
            searchInputRef.current.focus();
            setIsGridFocused(false);
          }
          break;
      }
    };
    
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handleNavigation, showSettings, filteredGames, focusedIndex, selectedGame, handleLaunch]);

  const themeClasses = darkMode ? "bg-slate-900 text-white" : "bg-gray-100 text-gray-900";
  const cardBase = "relative rounded-xl overflow-hidden shadow-lg transition-all duration-300 transform aspect-[2/3] flex flex-col justify-end group cursor-pointer border-2";

  // Loading screen
  if (isLoading) {
    return (
      <div className={`w-full min-h-screen flex flex-col items-center justify-center font-sans ${themeClasses} transition-colors duration-300`}>
        <PulsingGlowStyles />
        <Loader2 className="w-16 h-16 text-indigo-500 animate-spin-slow" />
        <h2 className="text-xl font-semibold mt-4">Loading CloudBox Games...</h2>
        <p className="opacity-70 mt-2">Fetching games from the cloud</p>
      </div>
    );
  }

  // Main render
  return (
    <div className={`w-full min-h-screen font-sans ${themeClasses} transition-colors duration-300 overflow-x-hidden`}>
      <PulsingGlowStyles />
      
      {/* Header */}
      <header className={`fixed top-0 left-0 right-0 z-40 px-6 py-4 flex items-center justify-between backdrop-blur-md ${darkMode ? 'bg-slate-900/80 border-b border-slate-800' : 'bg-white/80 border-b border-gray-200'}`}>
        <div className="flex items-center gap-3">
          <div className="bg-gradient-to-br from-blue-500 to-purple-600 p-2 rounded-lg shadow-lg">
            <Cloud className="text-white w-6 h-6" />
          </div>
          <h1 className="text-2xl font-bold tracking-tight">CloudBox</h1>
        </div>

        <div className="flex items-center gap-4">
          <div className={`flex items-center px-3 py-2 rounded-full border transition-colors ${darkMode ? 'bg-slate-800 border-slate-700' : 'bg-gray-50 border-gray-300'}`}>
            <Search className="w-4 h-4 opacity-50 mr-2" />
            <input 
              ref={searchInputRef}
              type="text" 
              placeholder="Search games..." 
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onFocus={() => setIsGridFocused(false)}
              onBlur={() => setIsGridFocused(true)}
              className={`bg-transparent border-none outline-none text-sm w-48 ${darkMode ? 'placeholder-slate-400' : 'placeholder-gray-500'}`}
            />
            {searchQuery && (
              <button onClick={() => setSearchQuery("")}>
                <X className="w-4 h-4 opacity-50 hover:opacity-100" />
              </button>
            )}
          </div>
          <button 
            onClick={() => setShowSettings(true)}
            className={`p-2 rounded-full transition-colors ${darkMode ? 'hover:bg-slate-800' : 'hover:bg-gray-200'}`}
          >
            <Settings className="w-6 h-6" />
          </button>
        </div>
      </header>

      {/* Filter Buttons */}
      <div className="pt-24 px-6 pb-4">
        <div className="flex gap-3">
          <button 
            onClick={() => setServices({ xbox: true, gfn: true })}
            className={`px-4 py-2 rounded-full font-medium transition-colors ${services.xbox && services.gfn ? 'bg-indigo-600 text-white' : darkMode ? 'bg-slate-800 text-slate-300 hover:bg-slate-700' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
          >
            All Games
          </button>
          <button 
            onClick={() => setServices({ xbox: false, gfn: true })}
            className={`px-4 py-2 rounded-full font-medium transition-colors flex items-center gap-2 ${services.gfn && !services.xbox ? 'bg-[#76B900] text-white' : darkMode ? 'bg-slate-800 text-slate-300 hover:bg-slate-700' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
          >
            <GfnLogo className="w-4 h-4" />
            GeForce NOW
          </button>
          <button 
            onClick={() => setServices({ xbox: true, gfn: false })}
            className={`px-4 py-2 rounded-full font-medium transition-colors flex items-center gap-2 ${services.xbox && !services.gfn ? 'bg-[#107C10] text-white' : darkMode ? 'bg-slate-800 text-slate-300 hover:bg-slate-700' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
          >
            <XboxLogo className="w-4 h-4" />
            Xbox Cloud
          </button>
        </div>
      </div>

      {/* Main Grid */}
      <main 
        className="px-6 pb-12 container max-w-7xl mx-auto transition-filter duration-300" 
        style={{ 
            filter: (selectedGame || showSettings) ? 'blur(2px)' : 'none',
            pointerEvents: (selectedGame || showSettings) ? 'none' : 'auto' 
        }}
      >
        {filteredGames.length === 0 ? (
          <div className="text-center mt-20 opacity-50 flex flex-col items-center">
            <Gamepad2 className="w-16 h-16 mb-4" />
            <p className="text-xl">No games found with the current filters</p>
            <p className="text-sm opacity-70 mt-2">Try changing your search or enabling more services</p>
          </div>
        ) : (
          <div 
            ref={gridRef}
            className="grid grid-cols-[repeat(auto-fill,minmax(200px,1fr))] gap-6 transition-all duration-300"
          >
            {filteredGames.map((game, index) => {
              const isVisuallyActive = index === focusedIndex && !selectedGame && !showSettings;
              const dimClass = (selectedGame || showSettings) ? 'opacity-60' : '';
              const activeClasses = isVisuallyActive ? 'is-visually-active' : 'opacity-90 hover:opacity-100';
              const badgeClasses = game.service === 'xbox' ? 'bg-[#107C10] text-white' : 'bg-[#76B900] text-white';
              const borderColor = game.service === 'xbox' ? 'border-blue-500' : 'border-green-500';

              return (
                <div 
                  key={game.id}
                  data-game-card
                  tabIndex={-1}
                  onClick={() => {
                    if (showSettings) return;
                    setFocusedIndex(index);
                    setSelectedGame(game);
                  }}
                  onMouseEnter={() => {
                    if (!showSettings) {
                      setFocusedIndex(index);
                      setIsGridFocused(false);
                    }
                  }}
                  className={`${cardBase} ${borderColor} ${activeClasses} ${dimClass}`}
                >
                  {/* Background blur */}
                  <div 
                    className="absolute inset-0"
                    style={{
                      backgroundImage: `url(${game.imageUrl})`,
                      backgroundSize: 'cover',
                      backgroundPosition: 'center',
                      filter: 'blur(10px)',
                      transform: 'scale(1.1)'
                    }}
                  />
                  <div className="absolute inset-0 bg-black/30" />

                  {/* Main image */}
                  <img 
                    src={game.imageUrl} 
                    alt={game.title}
                    className="absolute inset-0 w-full h-full object-cover opacity-95 group-hover:opacity-100"
                    onError={(e) => {
                      e.target.onerror = null;
                      const parent = e.target.parentNode;
                      const serviceColor = game.service === 'xbox' ? '#0078D4' : '#107C10';
                      parent.innerHTML = `
                        <div class="absolute inset-0 flex flex-col items-center justify-center bg-gradient-to-br ${game.color} p-4">
                          <span class="text-white font-bold text-lg text-center">${game.title}</span>
                          <span class="text-white/70 text-sm mt-2">${game.service === 'xbox' ? 'Xbox Cloud' : 'GeForce NOW'}</span>
                        </div>
                      `;
                    }}
                  />

                  {/* Gradient overlay */}
                  <div 
                    className="absolute inset-x-0 bottom-0"
                    style={{
                      height: '50%',
                      background: 'linear-gradient(to top, rgba(0,0,0,1) 0%, rgba(0,0,0,0.8) 0%, transparent 100%)'
                    }}
                  />

                  {/* Title */}
                  <div className="absolute bottom-0 left-0 right-0 p-4">
                    <h3 className="text-xl font-bold leading-tight text-white drop-shadow-[0_2px_8px_rgba(0,0,0,0.9)]">
                      {game.title}
                    </h3>
                    <div className="flex items-center justify-between mt-2">
                      <span className="text-sm text-white/80">{game.year}</span>
                      <span className="text-yellow-400 font-semibold"> {game.score}</span>
                    </div>
                  </div>

                  {/* Platform badge */}
                  <div className={`absolute top-2 left-2 p-1 rounded-full shadow-lg ${badgeClasses}`}>
                    {game.service === 'xbox' 
                      ? <XboxLogo className="w-4 h-4" /> 
                      : <GfnLogo className="w-4 h-4" />
                    }
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </main>
      
      {/* Controller Guide */}
      {!selectedGame && !showSettings && (
          <ControllerGuide darkMode={darkMode} />
      )}

      {/* Modals */}
      <SettingsModal
        show={showSettings}
        onClose={() => setShowSettings(false)}
        darkMode={darkMode}
        setDarkMode={setDarkMode}
        services={services}
        setServices={setServices}
        sortAsc={sortAsc}
        setSortAsc={setSortAsc}
      />

      <GameDetailModal 
        game={selectedGame}
        onClose={() => setSelectedGame(null)}
        onLaunch={handleLaunch}
        darkMode={darkMode}
      />
    </div>
  );
}
