// Pulsing Glow Styles
const PulsingGlowStyles = () => (
    <style dangerouslySetInnerHTML={{ __html: `
        @keyframes pulse-glow {
            0%, 100% { 
                box-shadow: 
                    0 0 5px rgba(99, 102, 241, 0.3), 
                    0 0 10px rgba(99, 102, 241, 0.15); 
            }
            50% { 
                box-shadow: 
                    0 0 10px rgba(129, 140, 248, 0.5), 
                    0 0 20px rgba(129, 140, 248, 0.3); 
            }
        }
        
        @keyframes wave-move {
          0% {
            background-position: 0% 0%;
          }
          100% {
            background-position: 200% 0%;
          }
        }
        
        @keyframes launch-pulse {
          0% {
            transform: scale(1);
            box-shadow: 0 0 0 0 rgba(99, 102, 241, 0.7);
          }
          70% {
            transform: scale(1.02);
            box-shadow: 0 0 0 10px rgba(99, 102, 241, 0);
          }
          100% {
            transform: scale(1);
            box-shadow: 0 0 0 0 rgba(99, 102, 241, 0);
          }
        }
        
        @keyframes float {
          0%, 100% {
            transform: translateY(0px);
          }
          50% {
            transform: translateY(-5px);
          }
        }
        
        @keyframes fade-in {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }
        
        @keyframes controller-pulse {
          0%, 100% {
            opacity: 0.6;
          }
          50% {
            opacity: 1;
          }
        }
        
        .is-visually-active {
            transform: scale(1.02) !important;
            z-index: 20; 
            border-color: #6366F1;
            animation: pulse-glow 2s infinite alternate ease-in-out;
        }
        
        .wave-animation {
          animation: wave-move 20s linear infinite;
        }
        
        .float-animation {
          animation: float 6s infinite ease-in-out;
        }
        
        .launch-pulse {
          animation: launch-pulse 2s infinite;
        }
        
        .controller-pulse {
          animation: controller-pulse 2s infinite;
        }
        
        .fade-in {
          animation: fade-in 0.5s ease-out;
        }
        
        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }
        
        .animate-spin-slow {
            animation: spin 3s linear infinite;
        }
        
        /* Hide scrollbar for Chrome, Safari and Opera */
        .no-scrollbar::-webkit-scrollbar {
          display: none;
        }
        
        /* Hide scrollbar for IE, Edge and Firefox */
        .no-scrollbar {
          -ms-overflow-style: none;  /* IE and Edge */
          scrollbar-width: none;  /* Firefox */
        }
        
        /* Retro wave pattern */
        .retro-wave-bg {
          background-color: #0f172a;
          background-image: 
            repeating-linear-gradient(90deg, 
              rgba(30, 64, 175, 0.1) 0px, 
              rgba(30, 64, 175, 0.1) 1px, 
              transparent 1px, 
              transparent 20px
            ),
            repeating-linear-gradient(0deg, 
              rgba(30, 64, 175, 0.1) 0px, 
              rgba(30, 64, 175, 0.1) 1px, 
              transparent 1px, 
              transparent 20px
            ),
            linear-gradient(135deg, 
              rgba(30, 64, 175, 0.1) 25%, 
              transparent 25%
            ),
            linear-gradient(135deg, 
              rgba(30, 64, 175, 0.1) 25%, 
              transparent 25%
            ),
            linear-gradient(45deg, 
              transparent 75%, 
              rgba(30, 64, 175, 0.1) 75%
            ),
            linear-gradient(45deg, 
              transparent 75%, 
              rgba(30, 64, 175, 0.1) 75%
            );
          background-size: 40px 40px, 40px 40px, 20px 20px, 20px 20px, 20px 20px, 20px 20px;
          background-position: 0 0, 0 0, 0 0, 10px 10px, 0 0, 10px 10px;
        }
        
        .wave-effect {
          background: linear-gradient(
            90deg,
            transparent,
            rgba(59, 130, 246, 0.1),
            rgba(99, 102, 241, 0.2),
            rgba(139, 92, 246, 0.1),
            transparent
          );
          background-size: 200% 100%;
          animation: wave-move 15s linear infinite;
        }
    `}} />
);

// Enhanced Controller Guide
