    const buttonBorder = darkMode 
      ? 'border-indigo-400/60 text-white bg-gradient-to-br from-slate-800 to-slate-900' 
      : 'border-indigo-500 text-gray-800 bg-gradient-to-br from-white to-gray-100';
    
    return (
        <div className={`fixed bottom-6 right-6 z-50 p-4 rounded-2xl backdrop-blur-xl border-2 ${darkMode 
          ? 'border-white/10 bg-gradient-to-br from-slate-900/70 to-slate-800/70 text-white shadow-2xl shadow-indigo-900/20' 
          : 'border-gray-300/50 bg-gradient-to-br from-white/80 to-gray-100/80 text-gray-900 shadow-2xl shadow-gray-400/20'} transition-all duration-300`}>
            
            {/* Subtle decorative elements */}
            <div className={`absolute -top-1 -right-1 w-4 h-4 rounded-full ${darkMode ? 'bg-indigo-500/30' : 'bg-indigo-400/30'}`}></div>
            <div className={`absolute -bottom-1 -left-1 w-3 h-3 rounded-full ${darkMode ? 'bg-purple-500/30' : 'bg-purple-400/30'}`}></div>
            
            {/* Title with bottom padding */}
            <div className={`absolute -top-2 left-4 px-2 py-0.5 rounded-full text-xs font-bold mb-2 flex items-center gap-1.5 ${darkMode 
              ? 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white' 
              : 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white'}`}>
              <GamepadIcon className="w-3 h-3" />
              {gamepadConnected ? 'Controller' : 'Keyboard'}
