};

// Game Detail Modal
const GameDetailModal = ({ game, onClose, onLaunch, darkMode }) => {
    if (!game) return null;

    const modalContentClasses = darkMode 
        ? 'bg-gradient-to-br from-slate-800 to-slate-900 text-white border border-slate-700' 
        : 'bg-gradient-to-br from-white to-gray-50 text-gray-900 border border-gray-200 shadow-2xl';

    return (
        <div 
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 transition-opacity duration-300 overflow-y-auto p-4"
            onClick={onClose}
        >
            <div className="absolute inset-0 bg-black/60" onClick={onClose} />
            <div 
                className={`relative w-full max-w-2xl max-h-[90vh] rounded-2xl p-6 transition-transform duration-300 transform scale-100 ${modalContentClasses} flex flex-col`}
                onClick={(e) => e.stopPropagation()}
            >
                <div className="flex justify-between items-start mb-6 flex-shrink-0">
                    <div className="flex flex-col">
                        <h2 className="text-3xl font-extrabold leading-tight bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                            {game.title}
                        </h2>
                    </div>
                    <button onClick={onClose} className={`p-2 rounded-full transition-colors ${darkMode ? 'hover:bg-slate-700' : 'hover:bg-gray-200'}`}>
                        <X className="w-6 h-6" />
                    </button>
                </div>

                <div className="relative flex-grow overflow-y-auto pr-2">
                    <div className="flex flex-col md:flex-row gap-6">
                        <div className="flex-shrink-0 mb-4 md:mb-0 rounded-xl overflow-hidden shadow-xl aspect-[2/3] w-full max-w-[150px] self-center md:self-start border-4 border-white/20">
                            <img 
                                src={game.imageUrl} 
                                alt={`${game.title} cover`} 
                                className="w-full h-full object-cover"
                                onError={(e) => {
                                    e.target.onerror = null;
                                    const parent = e.target.parentNode;
                                    parent.innerHTML = `
                                        <div class="w-full h-full flex flex-col items-center justify-center bg-gradient-to-br ${game.color} p-4">
                                            <ImageOff class="w-10 h-10 text-white/50 mb-2" />
                                            <span class="text-white/70 text-sm text-center">${game.title}</span>
                                        </div>
                                    `;
                                }}
                            />
                        </div>

                        <div className="flex-1">
                            <div className="flex flex-wrap gap-2 mb-6">
                                <div className={`flex items-center gap-1 text-sm px-3 py-1 rounded-full ${darkMode ? 'bg-slate-700/80' : 'bg-gradient-to-r from-blue-50 to-indigo-50 text-blue-700 border border-blue-200'}`}>
                                    <Calendar className="w-4 h-4" />
                                    <span>{game.year}</span>
                                </div>
                                <div className={`flex items-center gap-1 text-sm px-3 py-1 rounded-full ${darkMode ? 'bg-slate-700/80' : 'bg-gradient-to-r from-purple-50 to-pink-50 text-purple-700 border border-purple-200'}`}>
                                    <Layers className="w-4 h-4" />
                                    <span>{game.genre}</span>
                                </div>
                                <div className={`flex items-center gap-1 text-sm px-3 py-1 rounded-full ${game.service === 'xbox' ? 'bg-gradient-to-r from-[#107C10]/90 to-[#0e6e0e]/90' : 'bg-gradient-to-r from-[#76B900]/90 to-[#68a500]/90'} text-white shadow-md`}>
                                    {game.service === 'xbox' ? <XboxLogo className="w-4 h-4" /> : <GfnLogo className="w-4 h-4" />}
                                    <span>{game.service === 'xbox' ? 'Xbox Cloud' : 'GeForce NOW'}</span>
                                </div>
                            </div>

                            <h3 className="text-lg font-semibold mb-2 opacity-70">About the Game</h3>
                            <p className="text-sm leading-relaxed mb-8 opacity-90">{game.description}</p>
                            
                            <div className="grid grid-cols-2 gap-4 mb-6">
                                <div>
                                    <h4 className="text-sm font-semibold mb-1 opacity-70">Publisher</h4>
                                    <p className={`text-sm ${darkMode ? 'text-white/80' : 'text-gray-700'}`}>{game.publisher}</p>
                                </div>
                                <div>
                                    <h4 className="text-sm font-semibold mb-1 opacity-70">Release Year</h4>
                                    <p className={`text-sm ${darkMode ? 'text-white/80' : 'text-gray-700'}`}>{game.year}</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="flex-shrink-0 pt-4 flex gap-4">
                    <button 
                        onClick={() => {
                            console.log('Launching game:', game.title, 'to:', game.url);
                            if (game.url && game.url !== '#') {
                                window.open(game.url, '_blank', 'noopener,noreferrer');
                            }
                            onClose();
                        }}
                        className="w-full flex items-center justify-center gap-2 p-4 rounded-xl text-lg font-bold text-white bg-gradient-to-r from-blue-600 via-indigo-600 to-purple-600 hover:from-blue-700 hover:via-indigo-700 hover:to-purple-700 transition-all duration-200 shadow-lg shadow-indigo-500/30 transform hover:scale-[1.02] hover:shadow-indigo-500/50 active:scale-[0.98] launch-pulse"
                    >
                        <Play className="w-5 h-5" />
                        Launch Now
                        <ChevronRight className="w-5 h-5" />
                    </button>
                </div>
            </div>
        </div>
    );
};

// Settings Modal
const SettingsModal = ({ show, onClose, darkMode, setDarkMode, services, setServices, sortAsc, setSortAsc }) => {
    if (!show) return null;

    const modalClasses = darkMode 
        ? 'bg-gradient-to-br from-slate-800 to-slate-900 text-white border border-slate-700' 
        : 'bg-gradient-to-br from-white to-gray-50 text-gray-900 border border-gray-200 shadow-2xl';

    return (
        <div 
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm transition-opacity duration-300"
            onClick={onClose}
        >
            <div 
                className={`w-full max-w-lg rounded-2xl p-6 transition-transform duration-300 transform scale-100 ${modalClasses}`}
                onClick={(e) => e.stopPropagation()}
            >
                <div className="flex justify-between items-center mb-6">
                    <h2 className="text-2xl font-bold flex items-center gap-2 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                        <Settings className="w-6 h-6" />
                        CloudBox Settings
                    </h2>
                    <button 
                        onClick={onClose} 
                        className={`p-2 rounded-full transition-colors ${darkMode ? 'hover:bg-slate-700' : 'hover:bg-gray-200'}`}
                    >
                        <X className="w-6 h-6" />
                    </button>
                </div>
                
                <div className={`mb-6 p-4 rounded-xl ${darkMode ? 'bg-slate-800/80' : 'bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-100'}`}>
                    <h3 className="text-lg font-semibold mb-2">Theme</h3>
                    <div className="flex items-center justify-between">
                        <span className={darkMode ? 'text-white/80' : 'text-gray-700'}>Toggle Dark Mode</span>
                        <button 
                            onClick={() => setDarkMode(!darkMode)}
                            className={`flex items-center p-2 rounded-full transition-all ${darkMode ? 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700' : 'bg-gradient-to-r from-gray-200 to-gray-300 text-gray-800 hover:from-gray-300 hover:to-gray-400 shadow-sm'}`}
                        >
                            {darkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
                            <span className="ml-2 text-sm font-medium">{darkMode ? 'Light Mode' : 'Dark Mode'}</span>
                        </button>
                    </div>
                </div>

                <div className={`mb-6 p-4 rounded-xl ${darkMode ? 'bg-slate-800/80' : 'bg-gradient-to-r from-purple-50 to-pink-50 border border-purple-100'}`}>
                    <h3 className="text-lg font-semibold mb-2 flex items-center gap-2"><Filter className="w-5 h-5" /> Filters</h3>
                    <div className="flex gap-4">
                        <button
                            onClick={() => setServices(prev => ({ ...prev, xbox: !prev.xbox }))}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${services.xbox 
                                ? 'bg-gradient-to-r from-[#107C10] to-[#0e6e0e] text-white shadow-md' 
                                : darkMode ? 'bg-slate-700 text-gray-300 hover:bg-slate-600' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                        >
                            <XboxLogo className="w-5 h-5 mr-2" />
                            Xbox Cloud
                        </button>
                        <button
                            onClick={() => setServices(prev => ({ ...prev, gfn: !prev.gfn }))}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${services.gfn 
                                ? 'bg-gradient-to-r from-[#76B900] to-[#68a500] text-white shadow-md' 
                                : darkMode ? 'bg-slate-700 text-gray-300 hover:bg-slate-600' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                        >
                            <GfnLogo className="w-5 h-5 mr-2" />
                            GeForce NOW
                        </button>
                    </div>
                </div>

                <div className={`mb-6 p-4 rounded-xl ${darkMode ? 'bg-slate-800/80' : 'bg-gradient-to-r from-green-50 to-emerald-50 border border-green-100'}`}>
                    <h3 className="text-lg font-semibold mb-2 flex items-center gap-2"><ArrowDownAZ className="w-5 h-5" /> Sorting</h3>
                    <div className="flex gap-4">
                        <button
                            onClick={() => setSortAsc(true)}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${sortAsc
                                ? 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-md' 
                                : darkMode ? 'bg-slate-700 text-gray-300 hover:bg-slate-600' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                        >
                            <ArrowUpAZ className="w-5 h-5 mr-2" />
                            A-Z (Ascending)
                        </button>
                        <button
                            onClick={() => setSortAsc(false)}
                            className={`flex items-center px-4 py-2 rounded-full font-semibold transition-all duration-200 ${!sortAsc
                                ? 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-md' 
                                : darkMode ? 'bg-slate-700 text-gray-300 hover:bg-slate-600' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                        >
                            <ArrowDownAZ className="w-5 h-5 mr-2" />
                            Z-A (Descending)
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

// Main App Component
export default function App() {
  const [games, setGames] = useState([]);
  const [filteredGames, setFilteredGames] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedGame, setSelectedGame] = useState(null);
  const [darkMode, setDarkMode] = useState(true);
  const [showSettings, setShowSettings] = useState(false);
  const [services, setServices] = useState({ xbox: true, gfn: true });
  const [sortAsc, setSortAsc] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [focusedIndex, setFocusedIndex] = useState(0);
  const [isGridFocused, setIsGridFocused] = useState(true);
  const [gamepadConnected, setGamepadConnected] = useState(false);
  const gridRef = useRef(null);
  const searchInputRef = useRef(null);
  const gamepadRef = useRef(null);
  const prevButtonsRef = useRef([]);

  // Fetch games on component mount
  useEffect(() => {
    const loadGames = async () => {
      try {
        const data = await fetchGames();
        console.log('Loaded games:', data.length);
        setGames(data);
      } catch (error) {
        console.error("Failed to fetch games:", error);
      } finally {
        setIsLoading(false);
      }
    };

    loadGames();
  }, []);

  // Filter and sort games
  useEffect(() => {
    if (isLoading) return;

    console.log('Filtering games with services:', services);

    let result = games.filter(g => {
      const matchesService = (g.service === 'xbox' && services.xbox) || 
                            (g.service === 'gfn' && services.gfn);
      const matchesSearch = searchQuery ? 
        g.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        g.genre.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (g.publisher && g.publisher.toLowerCase().includes(searchQuery.toLowerCase()))
        : true;
      
      return matchesService && matchesSearch;
    });

    console.log('Filtered to', result.length, 'games');

    result.sort((a, b) => {
      return sortAsc ? a.title.localeCompare(b.title) : b.title.localeCompare(a.title);
    });

    setFilteredGames(result);
    
    if (result.length > 0) {
        setFocusedIndex(prev => Math.min(prev, result.length - 1));
    } else {
        setFocusedIndex(0);
    }
    
    if (selectedGame && !result.find(g => g.id === selectedGame.id)) {
        setSelectedGame(null);
    }
  }, [games, services, sortAsc, searchQuery, selectedGame, isLoading]);

  // Scroll into view
  useEffect(() => {
    if (isGridFocused && gridRef.current && filteredGames.length > 0) {
      const cards = gridRef.current.querySelectorAll('[data-game-card]');
      if (cards[focusedIndex]) {
        cards[focusedIndex].scrollIntoView({ behavior: 'smooth', block: 'center', inline: 'nearest' });
      }
    }
  }, [focusedIndex, isGridFocused, filteredGames.length]);

  // Launch handler
  const handleLaunch = useCallback((game, directLaunch = false) => {
    if (!game) return;
    
    if (directLaunch) {
        console.log('Launching game:', game.title, 'to:', game.url);
        if (game.url && game.url !== '#') {
            window.open(game.url, '_blank', 'noopener,noreferrer');
        }
        setSelectedGame(null);
        return;
    }

    if (!selectedGame || selectedGame.id !== game.id) {
        setSelectedGame(game);
        return;
    }
  }, [selectedGame]);

  // Navigation handler
  const handleNavigation = useCallback((direction) => {
    if (showSettings || selectedGame || document.activeElement === searchInputRef.current) return;
    if (!isGridFocused) setIsGridFocused(true);

    const total = filteredGames.length;
    if (total === 0) return;

    let gridWidth = 4;
    if (gridRef.current) {
        const style = window.getComputedStyle(gridRef.current);
        const columns = style.getPropertyValue('grid-template-columns').split(' ').length;
        if (columns > 0) gridWidth = columns;
    }
    
    setFocusedIndex(prev => {
      let next = prev;
      switch (direction) {
        case 'UP': next = prev - gridWidth; break;
        case 'DOWN': next = prev + gridWidth; break;
        case 'LEFT': next = prev - 1; break;
        case 'RIGHT': next = prev + 1; break;
        default: break;
      }
      
      if (next < 0) next = 0;
      if (next >= total) next = total - 1;
      return next;
    });
  }, [filteredGames, isGridFocused, showSettings, selectedGame]);

  // Gamepad polling
  useEffect(() => {
    let animationFrameId;
    
    const pollGamepad = () => {
      const gamepads = navigator.getGamepads();
      const gamepad = gamepads[0]; // Get first connected gamepad
      
      if (gamepad) {
        if (!gamepadConnected) {
          setGamepadConnected(true);
          console.log('Gamepad connected:', gamepad.id);
        }
        
        gamepadRef.current = gamepad;
        
        // Check buttons
        const buttons = gamepad.buttons;
        const prevButtons = prevButtonsRef.current;
        
        buttons.forEach((button, index) => {
          const pressed = button.pressed || button.value > 0.5;
          
          // Only trigger on button press (not release)
          if (pressed && (!prevButtons[index] || !prevButtons[index].pressed)) {
            handleGamepadInput(index);
          }
          
          // Update previous button state
          if (!prevButtons[index]) {
            prevButtons[index] = { pressed };
          } else {
            prevButtons[index].pressed = pressed;
          }
        });
        
        // Check axes for D-pad/joystick
        const axes = gamepad.axes;
        const DEAD_ZONE = 0.5;
        
        // Left/Right on axis 0
        if (axes[0] < -DEAD_ZONE) {
          handleNavigation('LEFT');
        } else if (axes[0] > DEAD_ZONE) {
          handleNavigation('RIGHT');
        }
        
        // Up/Down on axis 1
        if (axes[1] < -DEAD_ZONE) {
          handleNavigation('UP');
        } else if (axes[1] > DEAD_ZONE) {
          handleNavigation('DOWN');
        }
        
      } else {
        if (gamepadConnected) {
          setGamepadConnected(false);
          gamepadRef.current = null;
        }
      }
      
      animationFrameId = requestAnimationFrame(pollGamepad);
    };
    
    animationFrameId = requestAnimationFrame(pollGamepad);
    
    // Gamepad connection events
    const handleGamepadConnected = (e) => {
      console.log('Gamepad connected:', e.gamepad.id);
      setGamepadConnected(true);
      gamepadRef.current = e.gamepad;
    };
    
    const handleGamepadDisconnected = (e) => {
      console.log('Gamepad disconnected:', e.gamepad.id);
      setGamepadConnected(false);
      gamepadRef.current = null;
    };
    
    window.addEventListener('gamepadconnected', handleGamepadConnected);
    window.addEventListener('gamepaddisconnected', handleGamepadDisconnected);
    
    return () => {
      cancelAnimationFrame(animationFrameId);
      window.removeEventListener('gamepadconnected', handleGamepadConnected);
      window.removeEventListener('gamepaddisconnected', handleGamepadDisconnected);
    };
  }, [gamepadConnected, handleNavigation]);

  // Gamepad input handler
  const handleGamepadInput = useCallback((buttonIndex) => {
    // Don't handle gamepad input when typing in search
    const isTypingInSearch = document.activeElement === searchInputRef.current;
    if (isTypingInSearch && buttonIndex !== 1) return; // Only B button works when typing
    
    const gameInFocus = filteredGames[focusedIndex];
    
    switch(buttonIndex) {
      case 0: // A button (Xbox: Bottom, PlayStation: Cross)
        if (selectedGame) {
          handleLaunch(selectedGame, true);
        } else if (gameInFocus) {
          setSelectedGame(gameInFocus);
        }
        break;
        
      case 1: // B button (Xbox: Right, PlayStation: Circle)
        if (selectedGame) {
          setSelectedGame(null);
        } else if (showSettings) {
          setShowSettings(false);
        } else if (isTypingInSearch) {
          searchInputRef.current.blur();
          setIsGridFocused(true);
        }
        break;
        
      case 2: // X button (Xbox: Left, PlayStation: Square)
        if (selectedGame) {
          handleLaunch(selectedGame, true);
        } else if (gameInFocus) {
          handleLaunch(gameInFocus, true);
        }
        break;
        
      case 3: // Y button (Xbox: Top, PlayStation: Triangle)
        if (!selectedGame && !showSettings && searchInputRef.current) {
          searchInputRef.current.focus();
          setIsGridFocused(false);
        }
        break;
        
      case 9: // Start button
        if (!selectedGame && !isTypingInSearch) {
          setShowSettings(true);
        }
        break;
        
      case 8: // Select button
        // Optional: Could be used for something else
        break;
        
      case 12: // D-pad up
        if (!selectedGame && !showSettings) {
          handleNavigation('UP');
        }
        break;
        
      case 13: // D-pad down
        if (!selectedGame && !showSettings) {
          handleNavigation('DOWN');
        }
        break;
        
      case 14: // D-pad left
        if (!selectedGame && !showSettings) {
          handleNavigation('LEFT');
        }
        break;
        
      case 15: // D-pad right
        if (!selectedGame && !showSettings) {
          handleNavigation('RIGHT');
        }
        break;
    }
  }, [selectedGame, showSettings, filteredGames, focusedIndex, handleLaunch, handleNavigation]);

  // Keyboard listeners - Updated to work alongside gamepad
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Don't handle keyboard if gamepad is connected and user is using it
      // But still allow Escape and typing in search
      if (gamepadConnected && 
          !['Escape', 'b', 'B', 'Tab', 'Enter'].includes(e.key) && 
          document.activeElement !== searchInputRef.current) {
        return;
      }

      // Handle Escape/B for closing modals
      if (e.key === 'Escape' || e.key.toLowerCase() === 'b') {
        e.preventDefault();
        if (selectedGame) {
          setSelectedGame(null);
        } else if (showSettings) {
          setShowSettings(false);
        } else if (document.activeElement === searchInputRef.current) {
          searchInputRef.current.blur();
          setIsGridFocused(true);
        }
        return;
      }

      // Check if user is typing in search input
      const isTypingInSearch = document.activeElement === searchInputRef.current;
      
      // Handle search focus (Y button)
      if (e.key.toLowerCase() === 'y' && !isTypingInSearch && !selectedGame && !showSettings) {
        e.preventDefault();
        if (searchInputRef.current) {
          searchInputRef.current.focus();
          setIsGridFocused(false);
        }
        return;
      }

      // Handle settings menu (Start button)
      if ((e.key === 'Menu' || e.key === 'ContextMenu') && !isTypingInSearch && !selectedGame) {
        e.preventDefault();
        setShowSettings(true);
        return;
      }

      // If typing in search, don't handle gamepad controls
      if (isTypingInSearch) {
        return;
      }

      // Prevent default for navigation keys only when not typing
      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', ' ', 'Enter'].includes(e.key)) {
        e.preventDefault();
      }

      // Handle Start/Enter for settings only when grid is focused
      if ((e.key === 'Enter' && !selectedGame && !showSettings && isGridFocused)) {
        e.preventDefault();
        setShowSettings(true);
        return;
      }
      
      if (showSettings) return;

      const gameInFocus = filteredGames[focusedIndex];

      switch(e.key) {
        case 'ArrowUp': 
        case 'ArrowDown': 
        case 'ArrowLeft': 
        case 'ArrowRight': 
          if (!selectedGame) handleNavigation(e.key.replace('Arrow', '').toUpperCase());
          break;
        case 'Enter': 
          if (selectedGame) {
            handleLaunch(selectedGame, true);
          } else if (gameInFocus) {
            setSelectedGame(gameInFocus);
          }
          break;
        case ' ': // Spacebar: Launch directly
          if (selectedGame) {
            handleLaunch(selectedGame, true);
          } else if (gameInFocus) {
            handleLaunch(gameInFocus, true);
          }
          break;
        case 'x': // X button: Launch directly
        case 'X': // X button (caps)
          if (selectedGame) {
            handleLaunch(selectedGame, true);
          } else if (gameInFocus) {
            handleLaunch(gameInFocus, true);
          }
          break;
      }
    };
    
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handleNavigation, showSettings, filteredGames, focusedIndex, selectedGame, handleLaunch, isGridFocused, gamepadConnected]);

  // Updated theme - just text colors, background is now fixed
  const themeClasses = darkMode 
    ? "text-white" 
    : "text-gray-900";
  
  const cardBase = "relative rounded-xl overflow-hidden shadow-lg transition-all duration-300 transform aspect-[2/3] flex flex-col justify-end group cursor-pointer border-2 border-transparent hover:border-white/20";

  // Loading screen
  if (isLoading) {
    return (
      <div className={`w-full min-h-screen flex flex-col items-center justify-center font-sans bg-gradient-to-br from-slate-900 to-slate-950 text-white transition-colors duration-300 overflow-hidden fade-in`}>
        <PulsingGlowStyles />
        <Loader2 className="w-16 h-16 text-indigo-500 animate-spin-slow" />
        <h2 className="text-xl font-semibold mt-4">Loading CloudBox Games...</h2>
        <p className="opacity-70 mt-2">Fetching games from the cloud</p>
      </div>
    );
  }

  // Main render
  return (
    <div className={`w-full min-h-screen font-sans ${themeClasses} transition-colors duration-300 overflow-x-hidden no-scrollbar fade-in`}>
      <PulsingGlowStyles />
      
      {/* Retro-style dark blue animated background */}
      <div className="fixed inset-0 z-0 overflow-hidden retro-wave-bg">
        {/* Animated wave overlay */}
        <div className="absolute inset-0 wave-effect opacity-20"></div>
        
        {/* Subtle diagonal lines */}
        <div className="absolute inset-0 opacity-[0.02]"
          style={{
            backgroundImage: `repeating-linear-gradient(
              45deg,
              transparent,
              transparent 10px,
              rgba(59, 130, 246, 0.1) 10px,
              rgba(59, 130, 246, 0.1) 20px
            )`,
          }}
        ></div>
      </div>
      
      {/* Header with everything in one line */}
      <header className={`fixed top-0 left-0 right-0 z-40 px-6 py-4 backdrop-blur-md flex items-center justify-between ${darkMode ? 'bg-slate-900/80 border-b border-slate-800' : 'bg-white/80 border-b border-gray-200 shadow-sm'}`}>
        {/* Left side: Logo and filter buttons */}
        <div className="flex items-center gap-6">
          <div className="flex items-center gap-3">
            <div className="bg-gradient-to-br from-blue-500 to-purple-600 p-2 rounded-xl shadow-lg float-animation">
              <Cloud className="text-white w-6 h-6" />
            </div>
            <h1 className="text-2xl font-bold tracking-tight bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              CloudBox
            </h1>
          </div>
          
          {/* Filter Buttons in same line */}
          <div className="flex gap-2">
            <button 
              onClick={() => setServices({ xbox: true, gfn: true })}
              className={`px-3 py-1.5 rounded-full text-sm font-medium transition-all ${services.xbox && services.gfn 
                ? 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-md' 
                : darkMode 
                  ? 'bg-slate-800 text-slate-300 hover:bg-slate-700' 
                  : 'bg-white text-gray-700 hover:bg-gray-100 shadow-sm border border-gray-300'}`}
            >
              All Games
            </button>
            <button 
              onClick={() => setServices({ xbox: false, gfn: true })}
              className={`px-3 py-1.5 rounded-full text-sm font-medium transition-all flex items-center gap-1.5 ${services.gfn && !services.xbox 
                ? 'bg-gradient-to-r from-[#76B900] to-[#68a500] text-white shadow-md' 
                : darkMode 
                  ? 'bg-slate-800 text-slate-300 hover:bg-slate-700' 
                  : 'bg-white text-gray-700 hover:bg-gray-100 shadow-sm border border-gray-300'}`}
            >
              <GfnLogo className="w-3.5 h-3.5" />
              GFN
            </button>
            <button 
              onClick={() => setServices({ xbox: true, gfn: false })}
              className={`px-3 py-1.5 rounded-full text-sm font-medium transition-all flex items-center gap-1.5 ${services.xbox && !services.gfn 
                ? 'bg-gradient-to-r from-[#107C10] to-[#0e6e0e] text-white shadow-md' 
                : darkMode 
                  ? 'bg-slate-800 text-slate-300 hover:bg-slate-700' 
                  : 'bg-white text-gray-700 hover:bg-gray-100 shadow-sm border border-gray-300'}`}
            >
              <XboxLogo className="w-3.5 h-3.5" />
              Xbox
            </button>
          </div>
        </div>

        {/* Right side: Search and settings */}
        <div className="flex items-center gap-4">
          <div className={`flex items-center px-3 py-1.5 rounded-full border transition-all ${darkMode ? 'bg-slate-800 border-slate-700' : 'bg-white border-gray-300 shadow-sm'}`}>
            <Search className={`w-4 h-4 mr-2 ${darkMode ? 'text-slate-400' : 'text-gray-400'}`} />
            <input 
              ref={searchInputRef}
              type="text" 
              placeholder="Search games..." 
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onFocus={() => setIsGridFocused(false)}
              onBlur={() => setIsGridFocused(true)}
              className={`bg-transparent border-none outline-none text-sm w-40 ${darkMode ? 'placeholder-slate-400' : 'placeholder-gray-500'}`}
            />
            {searchQuery && (
              <button onClick={() => setSearchQuery("")}>
                <X className={`w-4 h-4 ${darkMode ? 'text-slate-400 hover:text-white' : 'text-gray-400 hover:text-gray-600'}`} />
              </button>
            )}
          </div>
          <button 
            onClick={() => setShowSettings(true)}
            className={`p-2 rounded-full transition-colors ${darkMode ? 'hover:bg-slate-800' : 'hover:bg-gray-200 shadow-sm'}`}
          >
            <Settings className="w-6 h-6" />
          </button>
        </div>
      </header>

      {/* Main Grid */}
      <main 
        className="px-6 pb-12 container max-w-7xl mx-auto transition-filter duration-300 pt-20 no-scrollbar relative z-10" 
        style={{ 
            filter: (selectedGame || showSettings) ? 'blur(2px)' : 'none',
            pointerEvents: (selectedGame || showSettings) ? 'none' : 'auto' 
        }}
      >
        {filteredGames.length === 0 ? (
          <div className="text-center mt-20 opacity-50 flex flex-col items-center">
            <Gamepad2 className={`w-16 h-16 mb-4 ${darkMode ? 'text-slate-600' : 'text-gray-400'}`} />
            <p className="text-xl">No games found with the current filters</p>
            <p className="text-sm opacity-70 mt-2">Try changing your search or enabling more services</p>
          </div>
        ) : (
          <div 
            ref={gridRef}
            className="grid grid-cols-[repeat(auto-fill,minmax(200px,1fr))] gap-6 transition-all duration-300"
          >
            {filteredGames.map((game, index) => {
              const isVisuallyActive = index === focusedIndex && !selectedGame && !showSettings;
              const dimClass = (selectedGame || showSettings) ? 'opacity-60' : '';
              const activeClasses = isVisuallyActive ? 'is-visually-active' : 'opacity-90 hover:opacity-100';
              const badgeClasses = game.service === 'xbox' 
                ? 'bg-gradient-to-r from-[#107C10] to-[#0e6e0e] text-white' 
                : 'bg-gradient-to-r from-[#76B900] to-[#68a500] text-white';

              return (
                <div 
                  key={game.id}
                  data-game-card
                  tabIndex={-1}
                  onClick={() => {
                    if (showSettings) return;
                    setFocusedIndex(index);
                    setSelectedGame(game);
                  }}
                  onMouseEnter={() => {
                    if (!showSettings) {
                      setFocusedIndex(index);
                      setIsGridFocused(false);
                    }
                  }}
                  className={`${cardBase} ${activeClasses} ${dimClass} ${darkMode ? '' : 'shadow-md hover:shadow-lg'}`}
                >
                  {/* Background blur - SIMPLIFIED */}
                  <div 
                    className="absolute inset-0"
                    style={{
                      backgroundImage: `url(${game.imageUrl})`,
                      backgroundSize: 'cover',
                      backgroundPosition: 'center',
                    }}
                  />
                  <div className="absolute inset-0 bg-black/30" />

                  {/* Main image */}
                  <img 
                    src={game.imageUrl} 
                    alt={game.title}
                    className="absolute inset-0 w-full h-full object-cover opacity-95 group-hover:opacity-100"
                    onError={(e) => {
                      e.target.onerror = null;
                      const parent = e.target.parentNode;
                      const serviceColor = game.service === 'xbox' ? '#0078D4' : '#107C10';
                      parent.innerHTML = `
                        <div class="absolute inset-0 flex flex-col items-center justify-center bg-gradient-to-br ${game.color} p-4">
                          <span class="text-white font-bold text-lg text-center">${game.title}</span>
                          <span class="text-white/70 text-sm mt-2">${game.service === 'xbox' ? 'Xbox Cloud' : 'GeForce NOW'}</span>
                        </div>
                      `;
                    }}
                  />

                  {/* Gradient overlay */}
                  <div 
                    className="absolute inset-x-0 bottom-0"
                    style={{
                      height: '50%',
                      background: 'linear-gradient(to top, rgba(0,0,0,1) 0%, rgba(0,0,0,0.8) 0%, transparent 100%)'
                    }}
                  />

                  {/* Title */}
                  <div className="absolute bottom-0 left-0 right-0 p-4">
                    <h3 className="text-xl font-bold leading-tight text-white drop-shadow-[0_2px_8px rgba(0,0,0,0.9)]">
                      {game.title}
                    </h3>
                    <div className="flex items-center justify-between mt-2">
                      <span className="text-sm text-white/80">{game.year}</span>
                    </div>
                  </div>

                  {/* Platform badge */}
                  <div className={`absolute top-2 left-2 p-1 rounded-full shadow-lg ${badgeClasses}`}>
                    {game.service === 'xbox' 
                      ? <XboxLogo className="w-4 h-4" /> 
                      : <GfnLogo className="w-4 h-4" />
                    }
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </main>
      
      {/* Enhanced Controller Guide */}
      {!selectedGame && !showSettings && (
          <ControllerGuide darkMode={darkMode} gamepadConnected={gamepadConnected} />
      )}

      {/* Modals */}
      <SettingsModal
        show={showSettings}
        onClose={() => setShowSettings(false)}
        darkMode={darkMode}
        setDarkMode={setDarkMode}
        services={services}
        setServices={setServices}
        sortAsc={sortAsc}
        setSortAsc={setSortAsc}
      />

      <GameDetailModal 
        game={selectedGame}
        onClose={() => setSelectedGame(null)}
        onLaunch={handleLaunch}
        darkMode={darkMode}
      />
    </div>
  );
}
